//**************************************************************************************************************************************************************
// The following KQL Queryset is created to Bike point database
// All the data is readily available in 'Bikestream' table
// You can run a query by placing the cursor anywhere on the query text and clicking the run button (or Shift + Enter).
// KQL overview https://aka.ms/KQL_overview
// Quick KQL reference guide can be found here https://aka.ms/kql-quick-reference
//***********************************************************************************************************************************************************

// Count number of rows
Bikestream
| count

// View a small sample of records
Bikestream
| take 10

// Count the data points by Neighbourhood
Bikestream
| summarize count() by Neighbourhood
| render piechart

// How many bikes are present per street
Bikestream
| summarize countBikes=sum(No_Bikes) by Street
| order by countBikes
| render columnchart

// Which is the most popular bike point
Bikestream
| summarize SumOfBikes=sum(No_Bikes) by BikepointID
| top 1 by SumOfBikes desc

// Which streets have the highest number of empty docks
Bikestream
| summarize countEmptyDocks=sum(No_Empty_Docks) by Street
| top 5 by countEmptyDocks

// Available bike points
Bikestream
| where EnqueuedTime > ago(1h)
| summarize arg_max(EnqueuedTime, No_Empty_Docks) by BikepointID,Longitude, Latitude, ['available docks'] = strcat( No_Empty_Docks, ' @', BikepointID )
| project Longitude, Latitude, ['available docks']
| render scatterchart with (kind = map)

// Look at the point on a specific street
Bikestream
| where Street == "Strand"
| distinct BikepointID

// What are daily trends in bike point usage
Bikestream
| summarize countBikes=sum(No_Bikes) by bin(EnqueuedTime, 1d)
| render timechart
